mapper.select:
select a.id as "id", a.login_name as "login_name", a.login_password as "login_password", a.user_name as "user_name", a.session_id as "session_id", a.sex as "sex", a.email as "email", a.phone as "phone", a.head_address as "head_address", a.digital_account as "digital_account", a.type as "type", a.order_by as "order_by", a.status as "status", a.remark as "remark", a.remove_logo as "remove_logo", a.create_user as "create_user", a.create_time as "create_time", a.update_time as "update_time" from callCenter_telemarketer a where 1=1
and a.remove_logo=#{pd.remove_logo}
and a.login_name like concat('%', #{pd.login_name},'%')
and a.user_name like concat('%', #{pd.user_name},'%')
and a.status=#{pd.status}
and a.type=#{pd.type}
order by a.order_by asc, a.create_time desc

select a.id as "id", a.login_name as "login_name", a.login_password as "login_password", a.user_name as "user_name", a.session_id as "session_id", a.sex as "sex", a.email as "email", a.phone as "phone", a.head_address as "head_address", a.digital_account as "digital_account", a.type as "type", a.order_by as "order_by", a.status as "status", a.remark as "remark", a.remove_logo as "remove_logo", a.create_user as "create_user", a.create_time as "create_time", a.update_time as "update_time" from callCenter_telemarketer a where 1=1
and a.id=#{id}
and a.login_name=#{login_name}
