mapper.select:
SELECT a.id AS id, a.sn AS sn, a.app_id AS app_id, a.secret AS secret, a.`status` AS `status`, a.remark AS remark, a.remove_logo AS remove_logo, a.create_user AS create_user, a.create_time AS create_time, a.update_time AS update_time, b.id AS customer_id FROM customer_device a LEFT JOIN customer_user b ON a.sn = b.sn WHERE 1 = 1
and a.remove_logo=#{pd.remove_logo}
and a.sn like concat('%', #{pd.sn},'%')
and a.status=#{pd.status}
order by a.status asc, a.create_time desc

select a.id as "id", a.sn as "sn", a.app_id as "app_id", a.secret as "secret", a.status as "status", a.remark as "remark", a.remove_logo as "remove_logo", a.create_user as "create_user", a.create_time as "create_time", a.update_time as "update_time" from customer_device a where 1=1
and a.id =#{id}
and a.sn like concat('%', #{sn},'%')
