mapper.select:
select a.id as "id", a.`name` as "name", a.short_name as "short_name", a.parent_id as "parent_id", a.type as "type", a.order_by as "order_by", a.remark as "remark", a.remove_logo as "remove_logo", a.create_user as "create_user", a.create_time as "create_time", a.update_time as "update_time", b.user_name as "user_name", c.name as "parent_name" from system_dictionary as a inner join system_user as b on a.create_user = b.id left join system_dictionary c on c.id = a.parent_id where 1=1
and a.parent_id = #{pd.parent_id}
and a.name LIKE concat(concat('%', #{pd.name}),'%')
and a.remove_logo = #{pd.remove_logo}
and a.short_name like concat(concat('%', #{pd.short_name}),'%')
order by a.order_by ,a.update_time desc

select a.id as "id", a.name as "name", a.short_name as "short_name", a.parent_id as "parent_id", a.type as "type", a.order_by as "order_by", a.remark as "remark", a.remove_logo as "remove_logo", a.create_user as "create_user", a.create_time as "create_time", a.update_time as "update_time", b.user_name as "user_name", c.name as "parent_name" from system_dictionary as a left join system_user b on a.create_user = b.id left join system_dictionary c on c.id = a.parent_id where a.remove_logo ='N'
and a.parent_id = #{parent_id}
order by a.order_by ,a.update_time desc

select a.id as "id", a.name as "name", a.short_name as "short_name", a.parent_id as "parent_id", a.type as "type", a.order_by as "order_by", a.remark as "remark", a.remove_logo as "remove_logo", a.create_user as "create_user", a.create_time as "create_time", a.update_time as "update_time", b.user_name as "user_name", c.name as "parent_name" from system_dictionary as a left join system_user b on a.create_user = b.id left join system_dictionary c on c.id = a.parent_id where 1=1
and a.parent_id = #{parent_id}
and a.remove_logo = #{remove_logo}
order by a.order_by ,a.create_time desc
