mapper.select:
select a.id as "id", a.org_cascade as "org_cascade", a.name as "name", a.short_name as "short_name", a.type as "type", a.parent_id as "parent_id", a.order_by as "order_by", a.remark as "remark", a.remove_logo as "remove_logo", a.create_user as "create_user", a.create_time as "create_time", a.update_time as "update_time", b.user_name as "user_name" , c.name as "parent_name" from system_organize a left join system_user b on a.create_user = b.id left join system_organize c on c.id = a.parent_id where 1=1
and a.name like concat('%', #{pd.name},'%')
and a.parent_id = #{pd.parent_id}
and a.remove_logo = #{pd.remove_logo}
order by a.order_by,a.create_time desc

select a.id as "id", a.org_cascade as "org_cascade", a.name as "name", a.short_name as "short_name", a.type as "type", a.parent_id as "parent_id", a.order_by as "order_by", a.remark as "remark", a.remove_logo as "remove_logo", a.create_user as "create_user", a.create_time as "create_time", a.update_time as "update_time", b.user_name as "user_name" , c.name as "parent_name" from system_organize as a inner join system_user as b on a.create_user = b.id left join system_organize c on a.parent_id = c.id where 1=1
and a.remove_logo =#{remove_logo}
and a.parent_id = #{parent_id}
and a.id = #{id}
order by a.order_by,a.create_time desc

    	select
    		a.organize_id as "organize_id"
    		from system_user_organize a
    		left join system_organize b on a.organize_id = b.id
    		where a.user_id=#{user_id} 
    		
				and type=#{type}
