mapper.select:
select a.id as "id", a.`name` as "name", a.short_name as "short_name", a.type as "type", a.organize_id as "organize_id", a.order_by as "order_by", a.remark as "remark", a.remove_logo as "remove_logo", a.create_user as "create_user", a.create_time as "create_time", a.update_time as "update_time", b.user_name as "user_name" from system_role as a inner join system_user as b on a.create_user = b.id where 1=1
and a.remove_logo =#{remove_logo}
order by a.order_by asc,a.update_time desc

    	select count(*) from system_role 
    	where 1=1 
		
			and remove_logo =#{remove_logo}

select a.id as "id", a.name as "name", a.short_name as "short_name", a.type as "type", a.organize_id as "organize_id", a.order_by as "order_by", a.remark as "remark", a.remove_logo as "remove_logo", a.create_user as "create_user", a.create_time as "create_time", a.update_time as "update_time"
from system_role as a where 1=1
and a.id=#{id}

SELECT a.id as "id", a.role_id as "role_id", a.menu_id as "menu_id", a.type as "type", a.create_time as "create_time", a.create_user as "create_user" FROM system_role_menu AS a where 1=1
and a.role_id = #{role_id}
and a.menu_id = #{menu_id}

select DISTINCT b.*, c.id as "role_id" from system_menu b left join system_role_menu as a on a.menu_id=b.id left join system_role_menu c on b.id=c.menu_id and c.role_id =#{user_roleid} where b.parent_id = #{parentId} and b.remove_logo='N' and b.type =#{type}
and a.role_id in
#{item}
group by b.id order by b.order_by
